import React, { useEffect } from 'react';
import styled from 'styled-components';
import logo from './logo.png';
import starImage from './star.png'; // Путь к изображению

const StarsContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 2;
`;

const Star = styled.div`
  position: absolute;
  width: 40px; 
  height: 40px; 
  opacity: 1; 
  border-radius: 50%;
  background-image: url(${starImage}); // Используем изображение в качестве фона
`;

const generateRandomRotation = () => {
  return `rotate(${Math.random() * 360}deg)`;
};

const generateRandomPosition = () => {
  const x = Math.random() * 100;
  const y = Math.random() * 100;
  return `translate(${x}%, ${y}%)`;
};

const StarsBackground = () => {
  useEffect(() => {
    const numStars = 100;
    const container = document.getElementById('stars-container');

    if (container) {
      for (let i = 0; i < numStars; i++) {
        const star = document.createElement('div');
        star.classList.add('star');
        star.style.transform = generateRandomRotation() + ' ' + generateRandomPosition();

        const starImage = document.createElement('img');
        starImage.src ={logo};
        starImage.style.width = '10px';
        starImage.style.height = '10px';
        star.appendChild(starImage);

        container.appendChild(star);
      }
    }
  }, []);

  return <StarsContainer id="stars-container" />;
};

export default StarsBackground;
