import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import starImage from './star.png'; // Путь к изображению

const StarsContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 0;
`;

const Star = styled.img`
  position: absolute;
  width: 40px; 
  height: 40px; 
  opacity: 1; 
  border-radius: 50%;
`;

const generateRandomRotation = () => {
  return `rotate(${Math.random() * 360}deg)`;
};

const generateRandomSize = () => {
  const minSize = 20;
  const maxSize = 60;
  return Math.floor(Math.random() * (maxSize - minSize + 1) + minSize);
};

const generateRandomPosition = () => {
  const x = Math.random() * window.innerWidth;
  const y = Math.random() * window.innerHeight;
  const size = generateRandomSize();
  if (x >= window.innerWidth || y >= window.innerHeight) generateRandomPosition();
  return { x, y, size };
};


const StarsBackground = () => {
  const [stars, setStars] = useState([]);

  useEffect(() => {
    const numStars = 100;
    const generatedStars = [];

    for (let i = 0; i < numStars; i++) {
      const position = generateRandomPosition();
      generatedStars.push({
        rotation: generateRandomRotation(),
        position: generateRandomPosition(),
        size: position.size,
      });
    }


    setStars(generatedStars);
  }, []);

  return (
    <StarsContainer>
      {stars.map((star, index) => (
        <Star
          key={index}
          src={starImage}
          style={{
            transform: `${star.rotation} translate(${star.x}px, ${star.y}px)`,
            width: `${star.size}px`,
            height: `${star.size}px`,
            posX: `${star.x}px`,
            posY: `${star.y}px`,
          }}
        />
      ))}

    </StarsContainer>
  );
};

export default StarsBackground;
