import React, { useEffect, useState } from 'react';
import Post from './post';
import './posts.css';

const Posts = () => {
  const [posts, setPosts] = useState([]);


  useEffect(() => {
    const temporaryData = [
      {
        id: 1,
        avatar: 'https://crosti.ru/patterns/00/17/01/73_picture_e6ef3066.jpg',
        username: 'user1',
        date: '2023-05-01',
        text: 'Текст поста 1',
        likes: 10,
        comments: 5
      },
      {
        id: 2,
        avatar: 'https://pixelbox.ru/wp-content/uploads/2022/05/smeshoy-kotik-avatar-pixelbox.ru-15-768x768.jpg',
        username: 'user2',
        date: '2023-05-02',
        text: 'Текст поста 2',
        likes: 5,
        comments: 2
      },
      {
        id: 3,
        avatar: 'https://fanibani.ru/images/wp-content/uploads/2021/09/162-8.jpg',
        username: 'user3',
        date: '2023-05-03',
        text: 'Текст поста 3',
        likes: 3,
        comments: 1
      }
    ];

    setPosts(temporaryData);
  }, []);

  return (
    <div className="posts-container">
      <div className="posts">
        {posts.map(post => (
          <Post
            key={post.id}
            avatar={post.avatar}
            username={post.username}
            date={post.date}
            text={post.text}
            likes={post.likes}
            comments={post.comments}
          />
        ))}
      </div>
    </div>
  );
};

export default Posts;
