import StarsBackground from '../components/stars';
import Header from '../components/header-auth';
import Sidebar from '../components/sidebar';

const SendSon = () => {
  const { userId } = useContext(AuthContext);
  const [dreamText, setDreamText] = useState('');

  const handleDreamTextChange = (event) => {
    setDreamText(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Отправка данных формы на сервер или выполнение другой логики
    console.log('UserId:', userId);
    console.log('Dream Text:', dreamText);

    // Сброс значений полей после отправки
    setDreamText('');
  };

  return (
    <>
     <StarsBackground />
     <Header />
     <Sidebar />
    </>
  );
};

export default SendSon;
