import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/header';
import StarsBackground from '../components/stars';
import Enter from '../components/enter';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(false);
    const navigate = useNavigate();

    const handleChange = (event) => {
        const { name, value } = event.target;
        if (name === 'email') {
            setEmail(value);
        } else if (name === 'password') {
            setPassword(value);
        }
        setError(false);
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        if (email === 'test@test.com' && password === 'test') {
            navigate('/main');
        } else {
            setError(true);
        }
    };

    return (
        <>
            <Header />
            <StarsBackground />
            <Enter />
            {error && <p>Неправильный пароль. Попробуйте еще раз.</p>}
            <form onSubmit={handleSubmit}>
                <input type="text" name="email" value={email} onChange={handleChange} placeholder="Email" />
                <input type="password" name="password" value={password} onChange={handleChange} placeholder="Пароль" />
                <button type="submit">Войти</button>
            </form>
        </>
    );
};

export default Login;
