import React, { useEffect } from 'react';
import logo from './logo.png';
import starImage from './star.png'; // Путь к изображению

const StarsBackground = () => {

  const background = document.createElement('div');
  background.style.position = 'absolute';
  background.style.width = '100%';
  background.style.height = '100%';
  background.style.top = '0';
  background.style.left = '0';
  document.body.appendChild(background);

  let starList = [];
  let starCounts = 10;
  for (let i = 0; i < starCounts; i++) {
    const star = {
      x: Math.random() * window.innerWidth, // Случайная координата X
      y: Math.random() * window.innerHeight, // Случайная координата Y
      size: Math.random() * 50 + 10, // Случайный размер от 10 до 60
      rotation: Math.random() * 360, // Случайный угол вращения от 0 до 360
    };
    starList.push(star);
  }

  starList.forEach((star) => {
    const starImage = document.createElement('img');
    starImage.src = starImage;
    starImage.style.position = 'absolute';
    starImage.style.left = star.x + 'px';
    starImage.style.top = star.y + 'px';
    starImage.style.width = star.size + 'px';
    starImage.style.height = star.size + 'px';
    starImage.style.transform = `rotate(${star.rotation}deg)`;
  
    background.appendChild(starImage);
  });

}

export default StarsBackground;
