import React, { Component } from 'react';
import Header from '../components/header';
import StarsBackground from '../components/stars';
import RegistrationForm from '../components/reg';

class Registration extends Component {
  constructor(props) {
    super(props);
    this.state = {
      avatar: '',
      firstName: '',
      lastName: '',
      email: '',
      birthDate: '',
      password: '',
      confirmPassword: '',
    };
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    // Здесь вы можете выполнить логику для обработки регистрации пользователя
    // Например, отправить данные на сервер для создания нового пользователя
    // Или выполнить другие действия, связанные с регистрацией пользователя

    // После успешной регистрации, вы можете перенаправить пользователя на другую страницу
    // с помощью объекта history или других методов роутинга в react-router-dom

    // Пример перенаправления на другую страницу:
    // this.props.history.push('/dashboard');
  };

  render() {
    const {
      avatar,
      firstName,
      lastName,
      email,
      birthDate,
      password,
      confirmPassword,
    } = this.state;

    return (
      <>
        <Header />
        <StarsBackground />
        <RegistrationForm
          handleChange={this.handleChange}
          handleSubmit={this.handleSubmit}
          avatar={avatar}
          firstName={firstName}
          lastName={lastName}
          email={email}
          birthDate={birthDate}
          password={password}
          confirmPassword={confirmPassword}
        />
      </>
    );
  }
}

export default Registration;
