import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import './sonForm.css';

const SendForm = ({ onSubmit, user }) => {
  const [dreamText, setDreamText] = useState('');

  const handleDreamTextChange = (event) => {
    setDreamText(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    onSubmit(dreamText);
    setDreamText('');
  };

  return (
    <Form className='sonForm' onSubmit={handleSubmit}>
      <div>
        <img src={user.avatar} alt="User Avatar" />
        <span>{user.name}</span>
      </div>
      <Form.Group controlId="dreamText">
        <Form.Label>Текст сна</Form.Label>
        <Form.Control
          as="textarea"
          rows={3}
          value={dreamText}
          onChange={handleDreamTextChange}
          placeholder="Введите текст своего сна"
          required
        />
      </Form.Group>
      <Button variant="primary" type="submit">
        Отправить
      </Button>
    </Form>
  );
};

export default SendForm;
