import React, { useState } from 'react';
import Header from '../components/header';
import StarsBackground from '../components/stars';
import Enter from '../components/enter';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';


const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (email, password) => {
    /*if (email === 'test@test.com' && password === 'test') {
      navigate('/Main');
    } else {
      // Показать сообщение об ошибке ввода
    }*/
    try {
      const response = await axios.post('/api/login', { email, password }); // Замените '/api/login' на путь к вашей API-конечной точке для входа
      if (response.status === 200) {
        console.log("logged");
        navigate('/Main');
      } else {
        console.log("error: go back");
      }
    } catch (error) {
      // Обработка ошибки AJAX-запроса
    }
  };

  return (
    <>
      <Header />
      <StarsBackground />
      <Enter handleLogin={handleLogin} />
    </>
  );
};

export default Login;
