import React, { Component } from 'react';

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: '',
    };
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    // Здесь вы можете выполнить логику для обработки входа пользователя
    // Например, отправить данные на сервер для проверки и аутентификации
    // Или выполнить другие действия, связанные с входом пользователя

    // После успешного входа, вы можете перенаправить пользователя на другую страницу
    // с помощью объекта history или других методов роутинга в react-router-dom

    // Пример перенаправления на другую страницу:
    // this.props.history.push('/dashboard');
  };

  render() {
    const { email, password } = this.state;

    return (
      <div>
        <h1>Вход</h1>
        <form onSubmit={this.handleSubmit}>
          <div>
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={email}
              onChange={this.handleChange}
              required
            />
          </div>
          <div>
            <label htmlFor="password">Пароль:</label>
            <input
              type="password"
              id="password"
              name="password"
              value={password}
              onChange={this.handleChange}
              required
            />
          </div>
          <button type="submit">Войти</button>
        </form>
      </div>
    );
  }
}

export default Login;
