import React, { useEffect, useState } from 'react';
import Header from '../components/header';

const Main = () => {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        fetchPosts(); // Вызываем функцию для получения списка постов при загрузке компонента
    }, []);

    const fetchPosts = () => {
        /* 
            fetch('https://api.example.com/posts')
          .then(response => response.json())
           .then(data => setPosts(data))
          .catch(error => console.log(error));
        */

        const dummyPosts = [
            { id: 1, title: 'Пост 1', content: 'Содержимое поста 1' },
            { id: 2, title: 'Пост 2', content: 'Содержимое поста 2' },
            { id: 3, title: 'Пост 3', content: 'Содержимое поста 3' }
        ];

        setPosts(dummyPosts);
    };

    return (
        <>
            <Header />
            <div className="post-feed">
                {posts.map(post => (
                    <div key={post.id} className="post">
                        <h2>{post.title}</h2>
                        <p>{post.content}</p>
                    </div>
                ))}
            </div>
        </>
    );
};

export default Main;
