import React, { useEffect } from 'react';
import styled from 'styled-components';

const StarsContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1;
`;

const Star = styled.img`
  position: absolute;
  width: 20px; /* Ширина звездочки */
  height: 20px; /* Высота звездочки */
  opacity: 0.7; /* Прозрачность звездочки (по желанию) */
`;

const generateRandomRotation = () => {
  return `rotate(${Math.random() * 360}deg)`;
};

const Stars = () => {
  useEffect(() => {
    const numStars = 100; // Количество звездочек, которые нужно создать
    const container = document.getElementById('stars-container');

    if (container) {
      for (let i = 0; i < numStars; i++) {
        const star = document.createElement('img');
        star.src = './star.png'; // Путь к изображению звезды
        star.classList.add('star');
        star.style.top = `${Math.random() * 100}%`;
        star.style.left = `${Math.random() * 100}%`;
        star.style.transform = generateRandomRotation();
        container.appendChild(star);
      }
    }
  }, []);

  return <StarsContainer id="stars-container" />;
};

export default Stars;
