import React, { Component } from 'react';
import Header from '../components/header';
import StarsBackground from '../components/stars';
import RegistrationForm from '../components/reg';

class Registration extends Component {
    constructor(props) {
        super(props);
        this.state = {
            avatar: '',
            firstName: '',
            lastName: '',
            email: '',
            birthDate: '',
            password: '',
            confirmPassword: '',
            passwordError: false,
        };
    }

    handleChange = (event) => {
        const { name, value } = event.target;

        // Проверка совпадения паролей и установка состояния ошибки
        if (name === 'confirmPassword' && value !== this.state.password) {
            this.setState({ passwordError: true });
        } else {
            this.setState({ passwordError: false });
        }

        this.setState({
            [event.target.name]: event.target.value,
        });
    };

    handleSubmit = (event) => {
        event.preventDefault();
        if (this.state.password !== this.state.confirmPassword) {
            // пароль не похож на повторный // 
            return;
        }
        // здеся будет бек // 
    };

    render() {
        const {
            avatar,
            firstName,
            lastName,
            email,
            birthDate,
            password,
            confirmPassword,
        } = this.state;

        return (
            <>
                <Header />
                <StarsBackground />
                <RegistrationForm
                    handleChange={this.handleChange}
                    handleSubmit={this.handleSubmit}
                    avatar={avatar}
                    firstName={firstName}
                    lastName={lastName}
                    email={email}
                    birthDate={birthDate}
                    password={password}
                    confirmPassword={confirmPassword}
                />
            </>
        );
    }
}

export default Registration;
