import React from 'react';
import './reg.css';
import arrow from './arrow.png';
import plus from './plus.png';

const RegistrationForm = ({
    avatar,
    firstName,
    lastName,
    email,
    birthDate,
    password,
    confirmPassword,
    handleChange,
    handleSubmit,
}) => {
    const handleAvatarChange = (event) => {
        const selectedFile = event.target.files[0];
        handleChange({ target: { name: 'avatar', value: URL.createObjectURL(selectedFile) } });
    };
    return (
        <div className='registration'>
            <form onSubmit={handleSubmit}>
                <div className='element' id='avatar-container'>
                    {avatar ? (
                        <img src={avatar} alt="Avatar" />
                    ) : (
                        <div className='default-avatar'>Выберите аватар</div>
                    )}
                    <input
                        type='file'
                        id='avatar'
                        name='avatar'
                        accept='image/*'
                        onChange={handleChange}
                        required
                    />
                    <img src={encodedImage} alt={plus} id='logo' />
                </div>
                <div className='element'>
                    <input
                        type='text'
                        id='firstName'
                        name='firstName'
                        placeholder='Имя'
                        value={firstName}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className='element'>
                    <input
                        type='text'
                        id='lastName'
                        name='lastName'
                        placeholder='Фамилия'
                        value={lastName}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className='element'>
                    <input
                        type='email'
                        id='email'
                        name='email'
                        placeholder='E-mail'
                        value={email}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className='element'>
                    <input
                        type='date'
                        id='birthDate'
                        name='birthDate'
                        value={birthDate}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className='element'>
                    <input
                        type='password'
                        id='password'
                        name='password'
                        placeholder='Пароль'
                        value={password}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className='element'>
                    <input
                        type='password'
                        id='confirmPassword'
                        name='confirmPassword'
                        placeholder='Повторите пароль'
                        value={confirmPassword}
                        onChange={handleChange}
                        required
                    />
                </div>
                <button type='submit'>
                    Зарегистрироваться <img src={arrow} alt='Arrow' />
                </button>
            </form>
        </div>
    );
};

export default RegistrationForm;
